package javaPackage;

public class Loop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 10; //Local variable
		
		while(i < 15 & i < 12) {
			System.out.println(i);
			i++; //if we forget to pass increment. program will execute till infinite
		}

	}

}
